    private void jBTSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTSalvarActionPerformed
        if (!jTFNome.getText().equals("")) {
            Cliente cliente = new Cliente();
            if (clienteId != -1) {
                cliente.setClienteId(clienteId);
            }
            cliente.setNome(jTFNome.getText());
            cliente.setEndereco(jTFEndereco.getText());
            cliente.setCidade(jTFCidade.getText());
            cliente.setEstado(jTFEstado.getText());
            cliente.setCep(jTFCEP.getText());
            cliente.setEscolaridade(jTFEscolaridade.getText());

            ClienteDAO clientedao = new ClienteDAOMySQL();
            if (clientedao.salvar(cliente)) {
                javax.swing.JOptionPane.showMessageDialog(null, "Cliente cadastrado com suscesso!");
            } else {
                javax.swing.JOptionPane.showMessageDialog(null, "Cliente n√£o cadastrado!");
            }
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "O Nome deve ser preenchido!");
            jTFNome.requestFocus();
        }
    }//GEN-LAST:event_jBTSalvarActionPerformed

