    private void jBTExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTExcluirActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser exclu√≠do");
        ClienteDAO clientedao = new ClienteDAOMySQL();
        Cliente cliente = clientedao.consultarPK(id);
        if (cliente != null) {
            jTFNome.setText(cliente.getNome());
            jTFEndereco.setText(cliente.getEndereco());
            jTFCidade.setText(cliente.getCidade());
            jTFEstado.setText(cliente.getEstado());
            jTFCEP.setText(cliente.getCep());
            jTFEscolaridade.setText(cliente.getEscolaridade());
            Object[] opcoes = {"Sim", "N√£o"};
            int resposta = javax.swing.JOptionPane.showOptionDialog(null, "Confirma a exclus√£o do Cliente?", "Exclus√£o", 0, javax.swing.JOptionPane.QUESTION_MESSAGE, null, opcoes, opcoes[1]);
            if (resposta == 0) {
                if (clientedao.apagarPK(Integer.parseInt(id))) {
                    javax.swing.JOptionPane.showMessageDialog(null, "Cliente exclu√≠do com suscesso!");
                    jTFCidade.setText("");
                    jTFEndereco.setText("");
                    jTFEscolaridade.setText("");
                    jTFEstado.setText("");
                    jTFCEP.setText("");
                    jTFNome.setText("");
                } else {
                    javax.swing.JOptionPane.showMessageDialog(null, "Cliente n√£o exclu√≠do!");
                }
            }
        }
    }//GEN-LAST:event_jBTExcluirActionPerformed

    private void JBTAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBTAlterarActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser alterado");
        ClienteDAO clientedao = new ClienteDAOMySQL();
        Cliente cliente = clientedao.consultarPK(id);
        if (cliente != null) {
            clienteId = cliente.getClienteId();
            jTFNome.setText(cliente.getNome());
            jTFEndereco.setText(cliente.getEndereco());
            jTFCidade.setText(cliente.getCidade());
            jTFEstado.setText(cliente.getEstado());
            jTFCEP.setText(cliente.getCep());
            jTFEscolaridade.setText(cliente.getEscolaridade());
        }
    }//GEN-LAST:event_JBTAlterarActionPerformed

